<?php

namespace DocBundle\Repository;

/**
 * CnpDocumentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ParametrageRepository extends \Doctrine\ORM\EntityRepository
{
    public function getParametrageWithReseau($reseauId)
    {
        $qb = $this->createQueryBuilder('p');
        $qb->join('p.partenaires', 'part')
            ->addSelect('part')
            ->where('part.id = :id')
            ->setParameter('id', $reseauId)
            ->orderBy('p.ordre');
        return $qb
            ->getQuery()
            ->getResult();
    }
}
